export const logs = writable<string[]>([]); // A store to keep log messages

export function addLog(message: string) {
    // Adds the message to the logs and keeps the latest ones
    logs.update(currentLogs => [...currentLogs, message]);
}

import { writable } from 'svelte/store';
import type { Component } from 'svelte';

interface ModalContent {
    content: Component;
    props?: any;
    header?: Component;
    footer?: Component;
}
export const modalVisible = writable(false);
export const modalContent = writable<ModalContent | null>(null);

export function openModal(
    content: Component,
    header?: Component,
    props?: any,
    footer?: Component
) {
    console.log("Opening modal with component:", content.name, "header:", header, "and props:", props);
    modalContent.set({ content, props, header, footer });
    modalVisible.set(true);
    console.log("Modal is now visible:", true);
}


export function closeModal() {
    console.log("Closing modal");
    modalContent.set(null);
    modalVisible.set(false);
    console.log("Modal is now visible:", false);
}

import { writable } from 'svelte/store';
import type { Component } from 'svelte';

export enum SidebarOrientation {
    Right = 'right',
    Left = 'left'
}

interface SidebarContent {
    component: Component;
    orientation: SidebarOrientation;
}

export const sidebarVisible = writable(false);
export const sidebarContent = writable<SidebarContent | null>(null);

export function openSidebar(component: Component, orientation: SidebarOrientation = SidebarOrientation.Right) {
    console.log("Opening sidebar with orientation:", orientation);
    sidebarContent.set({ component, orientation });
    sidebarVisible.set(true);
}

export function closeSidebar() {
    sidebarVisible.set(false);
    setTimeout(() => {
        sidebarContent.set(null);
    }, 300);
}

