import { SmartStyle } from './smart-style.svelte.js';

export class LinkHandler {

    private smartStyle: SmartStyle;

    constructor(smartStyle: SmartStyle) {
        this.smartStyle = smartStyle;
    }

    attachLink() {
        const url = prompt('Enter the URL:');
        const selection = window.getSelection();

        if (url && selection && selection.rangeCount > 0) {
            const range = selection.getRangeAt(0);
            if (this.smartStyle.isSmartStyleEnabled) {
                this.smartStyle.expandSelectionToWordBoundary(range);
            }

            if (range.toString().length === 0) {
                alert('Please select some text to attach the link.');
                return;
            }

            const link = document.createElement('a');
            link.href = url;
            link.target = '_blank';
            link.textContent = range.toString();

            range.deleteContents();
            range.insertNode(link);

            const newRange = document.createRange();
            newRange.setStartAfter(link);
            newRange.setEndAfter(link);
            selection.removeAllRanges();
            selection.addRange(newRange);
        } else {
            alert('No text selected to attach the link.');
        }
    }

    handlePaste(event: ClipboardEvent) {
        const clipboardData = event.clipboardData?.getData('text');
        const urlPattern = /(https?:\/\/[^\s]+)/g;

        if (clipboardData && urlPattern.test(clipboardData)) {
            event.preventDefault();
            const selection = window.getSelection();
            if (selection && selection.rangeCount > 0) {
                const range = selection.getRangeAt(0);
                if (this.smartStyle.isSmartStyleEnabled) {
                    this.smartStyle.expandSelectionToWordBoundary(range);
                }

                if (range.toString().length > 0) {
                    const link = document.createElement('a');
                    link.href = clipboardData;
                    link.target = '_blank';
                    link.textContent = range.toString();

                    range.deleteContents();
                    range.insertNode(link);

                    const newRange = document.createRange();
                    newRange.setStartAfter(link);
                    newRange.setEndAfter(link);
                    selection.removeAllRanges();
                    selection.addRange(newRange);
                } else {
                    document.execCommand('insertText', false, clipboardData);
                }
            }
        }
    }
}

// media-handler.ts
export class MediaHandler {
    insertTable(rows: number, columns: number) {
        const table = document.createElement('table');
        for (let i = 0; i < rows; i++) {
            const row = table.insertRow();
            for (let j = 0; j < columns; j++) {
                const cell = row.insertCell();
                cell.innerHTML = '&nbsp;'; // Add a space or placeholder
            }
        }
        this.insertElement(table);
    }

    insertImage(src: string, alt: string = '') {
        const img = document.createElement('img');
        img.src = src;
        img.alt = alt;
        this.insertElement(img);
    }

    private insertElement(element: HTMLElement) {
        const selection = window.getSelection();
        if (selection && selection.rangeCount > 0) {
            const range = selection.getRangeAt(0);
            range.deleteContents();
            range.insertNode(element);
        }
    }
}

export class SmartStyle {

    public smartStyleEnabled = $state(true);

    get isSmartStyleEnabled(): boolean {
        return this.smartStyleEnabled;
    }

    toggleSmartStyle() {
        this.smartStyleEnabled = !this.smartStyleEnabled;
    }

    // Expand the current text selection to the word boundaries when smart style is enabled
    expandSelectionToWordBoundary(range: Range) {
        const startContainer = range.startContainer;
        const endContainer = range.endContainer;

        const expandBoundary = (container: Node, offset: number, isStart: boolean) => {
            const text = container.textContent || '';
            let newOffset = offset;

            if (isStart) {
                // Move backward to the start of the word
                while (newOffset > 0 && /\w/.test(text[newOffset - 1])) {
                    newOffset--;
                }
            } else {
                // Move forward to the end of the word
                while (newOffset < text.length && /\w/.test(text[newOffset])) {
                    newOffset++;
                }
            }
            return newOffset;
        };

        // Expand the range to word boundaries
        const newStartOffset = expandBoundary(startContainer, range.startOffset, true);
        range.setStart(startContainer, newStartOffset);

        const newEndOffset = expandBoundary(endContainer, range.endOffset, false);
        range.setEnd(endContainer, newEndOffset);
    }
}

import { SmartStyle } from './smart-style.svelte.js';

export class TextFormatter {

    private smartStyle: SmartStyle;

    constructor(smartStyle: SmartStyle) {
        this.smartStyle = smartStyle;
    }

    private toggleStyle(command: string, value: string = '') {
        const selection = window.getSelection();
        if (selection && selection.rangeCount > 0) {
            const range = selection.getRangeAt(0);
            if (this.smartStyle.isSmartStyleEnabled) {
                this.smartStyle.expandSelectionToWordBoundary(range);
            }
            document.execCommand(command, false, value);
        }
    }

    toggleBold() {
        this.toggleStyle('bold');
    }

    toggleItalic() {
        this.toggleStyle('italic');
    }

    toggleUnderline() {
        this.toggleStyle('underline');
    }

    toggleHeading(level: number) {
        this.toggleStyle('formatBlock', `H${level}`);
    }

    toggleH2() {
        this.toggleHeading(2);
    }

    toggleH3() {
        this.toggleHeading(3);
    }

    toggleH4() {
        this.toggleHeading(4);
    }

    cycleHeading() {
        const selection = window.getSelection();
        if (selection && selection.rangeCount > 0) {
            const range = selection.getRangeAt(0);
            const parentElement = this.findClosestBlockParent(range.commonAncestorContainer);

            const headingLevels = ['H1', 'H2', 'H3', 'H4'];
            let nextHeadingIndex = 0; // Default to H1 if no heading is found

            if (parentElement) {
                const currentTag = parentElement.tagName.toUpperCase();
                const currentIndex = headingLevels.indexOf(currentTag);

                // Determine the next heading level
                nextHeadingIndex = (currentIndex + 1) % headingLevels.length;
            }

            this.toggleHeading(nextHeadingIndex + 1); // +1 to match H1, H2, etc.
        }
    }

    private findClosestBlockParent(node: Node): HTMLElement | null {
        let currentNode: Node | null = node;
        while (currentNode && currentNode.nodeType === Node.TEXT_NODE) {
            currentNode = currentNode.parentNode;
        }

        while (currentNode && (!(currentNode instanceof HTMLElement) || 
               !['H1', 'H2', 'H3', 'H4', 'P', 'DIV'].includes(currentNode.tagName.toUpperCase()))) {
            currentNode = currentNode.parentNode;
        }

        return currentNode as HTMLElement | null;
    }

    toggleAlignLeft() {
        this.toggleStyle('justifyLeft');
    }

    toggleAlignCenter() {
        this.toggleStyle('justifyCenter');
    }

    toggleAlignRight() {
        this.toggleStyle('justifyRight');
    }

    toggleAlignJustify() {
        this.toggleStyle('justifyFull');
    }

    toggleOrderedList() {
        this.toggleStyle('insertOrderedList');
    }

    toggleUnorderedList() {
        this.toggleStyle('insertUnorderedList');
    }

    isList(): boolean {
        const selection = window.getSelection();
        if (selection && selection.rangeCount > 0) {
            const range = selection.getRangeAt(0);
            const parentElement = this.findClosestBlockParent(range.commonAncestorContainer);
            return !!parentElement && ['UL', 'OL'].includes(parentElement.tagName.toUpperCase());
        }
        return false;
    }

    removeList() {
        if (this.isList()) {
            this.toggleStyle('insertUnorderedList'); // Toggle off unordered list
            this.toggleStyle('insertOrderedList');   // Toggle off ordered list
        }
    }

}

// modules/text-sanitizer.ts
import DOMPurify from 'dompurify';

export class TextSanitizer {
  private sanitizer: DOMPurify.DOMPurifyI;

  constructor() {
    this.sanitizer = DOMPurify as unknown as DOMPurify.DOMPurifyI;
  }

  sanitize(html: string): string {
    const sanitizedHtml = this.sanitizer.sanitize(html, {
      ALLOWED_TAGS: ['b', 'i', 'u', 'a', 'p', 'br', 'ul', 'ol', 'li', 'h1', 'h2', 'h3', 'h4', 'h5', 'h6', 'strong', 'em'],
      ALLOWED_ATTR: ['href', 'title', 'target', 'rel'],
    });

    return sanitizedHtml.replace(/<(\w+)(?:\s+[^>]*)?>\s*<\/\1>/g, '');
  }
}
